mapper.select:
SELECT * FROM books 
            (title ILIKE CONCAT('%', #{search}, '%') OR author ILIKE
       CONCAT('%', #{search}, '%') OR genre ILIKE CONCAT('%', #{search}, '%') OR CAST(publishedyear
       AS TEXT) ILIKE CONCAT('%', #{search}, '%')) 

    ORDER BY ${orderByColumn}
       ${orderByDirection} 
    LIMIT #{limit} 
    OFFSET #{offset}
