mapper.select:
SELECT * FROM movies 
            (title ILIKE CONCAT('%', #{search}, '%') OR director ILIKE
       CONCAT('%', #{search}, '%') OR genre ILIKE CONCAT('%', #{search}, '%') OR CAST(releaseyear
       AS TEXT) ILIKE CONCAT('%', #{search}, '%')) 

    ORDER BY ${orderByColumn}
       ${orderByDirection} 
    LIMIT #{limit} 
    OFFSET #{offset}
